const buildArrayScenes = $('Build Faceless Array').first().json.scenes;
const doneBranchItems = $input.all();

const updatedScenes = buildArrayScenes.map(scene => {

  const matchingVideoItem = doneBranchItems.find(item =>
    item.json?.id === scene.sceneId &&
    typeof item.json.response === 'string' && 
    item.json.response.startsWith('http')
  );

  if (matchingVideoItem) {
    return {
      ...scene,
      video: matchingVideoItem.json.response,
    };
  }

  return scene;
});

return [
  {
    json: {
      scenes: updatedScenes,
    },
  },
];

const items = $input.all();

const videoUrls = items.map((item) => ({
  video_url: item.json.response[0].file_url
}));

const output = {
  video_urls: videoUrls,
  id: "2323"
};

return [
  {
    json: {
      output: JSON.stringify(output)
    }
  }
];

const item = $input.first().json;

const scenes = [];

const keys = Object.keys(item);
const sceneNumbers = new Set();

keys.forEach(key => {
  const match = key.match(/(Audio|Image) (\d+)/);
  if (match) {
    sceneNumbers.add(match[2]);
  }
});

Array.from(sceneNumbers)
  .sort((a, b) => Number(a) - Number(b))
  .forEach(num => {
    scenes.push({
      audio: item[`Audio ${num}`] || null,
      image: item[`Image ${num}`] || null,
      video: null,
      sceneId: `Scene ${num}`,
    });
  });

return [
  {
    json: {
      scenes,
    },
  },
];

const items = [];
const inputItem = $input.first();

// Loop from audio_1 to audio_12
for (let i = 1; i <= 12; i++) {
  const key = `audio_${i}`;

  if (inputItem.json[key]) {
    items.push({
      json: {
        audio_script: inputItem.json[key],
      }
    });
  }
}

return items;

const items = [];
const inputItem = $input.first();
const initialImageUrl = "https://raw.githubusercontent.com/vamsi061/workflows/refs/heads/main/MojoMax.png";

// Loop from image_1 to image_12
for (let i = 1; i <= 12; i++) {
  const key = `image_${i}`;

  if (inputItem.json[key]) {
    items.push({
      json: {
        image_script: inputItem.json[key],
        image_index: i,
        previous_image: i === 1 ? initialImageUrl : null
      }
    });
  }
}

return items;

const combinedItem = {
  json: {}
};

// Combine all items into a single item
$input.all().forEach((item, index) => {
  const audioNumber = index + 1;
  combinedItem.json[`image_${audioNumber}`] = item.json.audio_script || item.json;
});

return [combinedItem];

const combinedItem = {
  json: {}
};

// Combine all items into a single item
$input.all().forEach((item, index) => {
  const audioNumber = index + 1;
  combinedItem.json[`audio_${audioNumber}`] = item.json.audio_script || item.json;
});

return [combinedItem];

// Get the current captioned video key from Caption Video node
let currentCaptionedKey = null;
try {
  const captionUrl = $('Caption Video').first().json.response;
  const match = captionUrl.match(/nca-toolkit\/(.+)$/);
  if (match) {
    currentCaptionedKey = match[1];
  }
} catch (e) {
  // Caption Video might not have response
}

// Get all S3 files and filter for deletion
const filesToDelete = [];
const items = $input.all();

items.forEach(item => {
  const key = item.json.Key;

  // Skip if this is the current captioned video
  if (currentCaptionedKey && key === currentCaptionedKey) {
    return; // Keep this file
  }

  // Delete all .mp4 and .mp3 files (old videos and audio)
  if (key.endsWith('.mp4') || key.endsWith('.mp3')) {
    filesToDelete.push({
      json: { key }
    });
  }
});

// Also add the audio folder paths to delete (they should be empty after files are deleted)
for (let i = 1; i <= 3; i++) {
  filesToDelete.push({
    json: { key: 'audio/elevenlabs_' + i + '/' }
  });
}

return filesToDelete;

// Get the output from Basic LLM Chain1
const inputItem = $input.first().json.output;

// Create an array to hold the 3 separate story items
const stories = [];

// Loop through the 3 stories and create separate items
for (let i = 1; i <= 3; i++) {
  const titleKey = `Title_${i}`;
  const scriptKey = `Story_Script_${i}`;

  if (inputItem[titleKey] && inputItem[scriptKey]) {
    stories.push({
      json: {
        title: inputItem[titleKey],
        script: inputItem[scriptKey]
      }
    });
  }
}

return stories;

// Get current item from Split in Batches
const currentItem = $input.first();

let previousImageUrl = null;

// Try to get the last generated image URL from Image Get node
try {
  const imageGetItems = $('Image Get').all();

  if (imageGetItems && imageGetItems.length > 0) {
    const lastImageItem = imageGetItems[imageGetItems.length - 1];
    previousImageUrl = lastImageItem.json?.data?.[0]?.url;
  }
} catch (error) {
  // First iteration - Image Get hasn't executed yet
}

// If no previous image found, use fallback
if (!previousImageUrl) {
  previousImageUrl = currentItem.json?.previous_image 
    || "https://raw.githubusercontent.com/vamsi061/workflows/refs/heads/main/MojoMax.png";
}

// Return updated item
return [{
  json: {
    image_script: currentItem.json.image_script,
    image_index: currentItem.json.image_index,
    previous_image: previousImageUrl
  }
}];
